interface SidenavProps {
  isSidebarOpen: boolean;
  toggleSidebar: () => void;
}

const AdminSidenav: React.FC<SidenavProps> = ({
  isSidebarOpen,
  toggleSidebar,
}) => {
  const navigate = useNavigate();
  const location = useLocation();
  const [openSubMenu, setOpenSubMenu] = useState<string | null>(null);
  const menus = [
    {
      title: "Dashboard",
      url: "/admin",
      sub: [],
      icon: <DashboardIcon />,
      selectedIcon: <DashboardIconSelected />,
    },
    {
      title: "Store",
      url: "/store",
      sub: [],
      icon: <StoreIcon />,
      selectedIcon: <StoreIcon />,
    },
    {
      title: "Employees",
      url: "/employees",
      sub: [],
      icon: <CustomerIcon />,
      selectedIcon: <CustomerIconSelected />,
    },
   
  ];
  // Function to handle toggling of sub-menu
  const handleSubMenuToggle = (menu: any, e: React.MouseEvent) => {
    e.preventDefault();

    if (menu.sub.length > 0) {
      if (openSubMenu === menu.title) {
        setOpenSubMenu(null);
      } else {
        setOpenSubMenu(menu.title);
      }
    }
    navigate(menu.url);
  };
  const handleNavigate = (menu: any, e: React.MouseEvent) => {
    // console.log("navigate");
    e.preventDefault();

    navigate(menu.url, { replace: true });
  };

  return (
    <div>
      <div className={`sidebartwo ${isSidebarOpen ? "close" : "open"}`}>
        <div className="logo">
          <PerkmasterLogo />
        </div>

        <ul className="list-unstyled">
          {menus.map((menu) => (
            <li
              key={menu.title}
              className={
                location.pathname.startsWith(menu.url) ? "selected" : ""
              }
            >
              <a
                href={menu.url}
                onClick={(e) => {
                  menu.sub.length > 0
                    ? handleSubMenuToggle(menu, e)
                    : navigate(menu.url);
                }}
              >
                {location.pathname === menu.url ? menu.selectedIcon : menu.icon}
              </a>
            </li>
          ))}
        </ul>
      </div>
      <div className={`sidebar ${isSidebarOpen ? "open" : "closed"}`}>
        <div className="logo">
          <PerkmasterBigLogo />
          <h3>Organization</h3>
        </div>
        <ul className="list-unstyled">
          {menus.map((menu) => (
            <li
              key={menu.title}
              className={
                location.pathname.startsWith(menu.url) ? "selected" : ""
              }
            >
              <a
                href="#"
                onClick={(e) => {
                  e.preventDefault();
                  if (menu.sub.length > 0) {
                    handleSubMenuToggle(menu, e);
                  } else {
                    handleNavigate(menu, e);
                  }
                }}
              >
                <div className="icons">
                  {location.pathname.startsWith(menu.url)
                    ? menu.selectedIcon
                    : menu.icon}
                  {menu.title}
                </div>
                {menu.sub.length > 0 && (
                  <i
                    className={`fas ${
                      openSubMenu === menu.title
                        ? "fa-caret-up"
                        : "fa-caret-down"
                    }`}
                  ></i>
                )}
              </a>

              {/* {menu.sub.length > 0 &&
                (openSubMenu === menu.title ||
                  menu?.sub?.some((sub) =>
                    location?.pathname?.startsWith(sub?.url)
                  )) && (
                  <ul className="submenu">
                    <ul className="submenu">
                      {menu?.sub?.map((subMenu) => (
                        <li
                          key={subMenu?.title}
                          className={
                            location.pathname.startsWith("/store/products") &&
                            subMenu?.url === "/store/products"
                              ? "selected"
                              : location?.pathname === subMenu?.url
                              ? "selected"
                              : ""
                          }
                        >
                          <a
                            href="#"
                            onClick={(e) => handleNavigate(subMenu, e)}
                          >
                            {subMenu?.title}
                          </a>
                        </li>
                      ))}
                    </ul>
                  </ul>
                )} */}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default AdminSidenav;
